// Helper to split paragraph into sentences and then words
const processText = (text: string, baseId: number): ListeningIntenseSentence[] => {
    // Basic sentence splitting (simplified)
    const rawSentences = text.match(/[^.!?]+[.!?]+/g) || [text];
    
    return rawSentences.map((sentence, index) => {
        const cleanSentence = sentence.trim();
        // Split by spaces but keep punctuation for display if needed
        const words = cleanSentence.split(/\s+/).map(w => w.replace(/[^\w']/g, "")); 
        
        return {
            id: `${baseId}-${index}`,
            text: cleanSentence,
            words: words.filter(w => w.length > 0)
        };
    });
};

